def my_func(input1: str):
    frames = []
    current_frame = []
    rolls = list(input1)

    for roll in rolls:
        if roll == 'X':
            frames.append([10])
            if len(frames) == 10:
                current_frame = frames[-1]
        else:
            if roll == '/':
                current_frame.append(10 - current_frame[-1])
            else:
                current_frame.append(int(roll))
            if len(current_frame) == 2 or len(frames) == 10:
                frames.append(current_frame)
                current_frame = []

    score = 0
    for i in range(10):
        frame = frames[i]
        if len(frame) == 1:
            score += 10
            if i < len(frames) - 1:
                next_frame = frames[i + 1]
                if len(next_frame) == 1:
                    score += 10 + (frames[i + 2][0] if i + 2 < len(frames) else 0)
                else:
                    score += sum(next_frame)
        elif sum(frame) == 10:
            score += 10 + (frames[i + 1][0] if i + 1 < len(frames) else 0)
        else:
            score += sum(frame)

    return score
