def my_func(input1: str):
    rolls = list(input1)
    score = 0
    frame = 0
    i = 0
    while frame < 10 and i < len(rolls):
        if rolls[i] == 'X':  # Strike
            score += 10
            if i + 1 < len(rolls):
                score += roll_value(rolls[i + 1])
            if i + 2 < len(rolls):
                score += roll_value(rolls[i + 2])
            frame += 1
            i += 1
        elif rolls[i] == '/':  # Spare
            score += 10 - roll_value(rolls[i - 1])
            if i + 1 < len(rolls):
                score += roll_value(rolls[i + 1])
            frame += 1
            i += 1
        else:  # Normal roll
            score += roll_value(rolls[i])
            if i + 1 < len(rolls) and rolls[i + 1] == '/':  # Spare in second roll
                i += 1
            else:
                frame += 0.5
            i += 1
    return score

def roll_value(roll):
    if roll == 'X':
        return 10
    elif roll == '-':
        return 0
    elif roll == '/':
        return 0  # Spare logic handled separately
    else:
        return int(roll)
