def my_func(input1: str):
    frames = []
    i = 0
    while i < len(input1):
        if len(frames) == 10:
            break
        if input1[i] == 'X':
            frames.append((10,))
            i += 1
        elif i + 1 < len(input1):
            first_roll = input1[i]
            second_roll = input1[i + 1]
            if second_roll == '/':
                frames.append((int(first_roll), 10 - int(first_roll)))
                i += 2
            else:
                frames.append((int(first_roll) if first_roll != '-' else 0,
                               int(second_roll) if second_roll != '-' else 0))
                i += 2
        else:
            break

    score = 0
    for f in range(len(frames)):
        frame = frames[f]
        score += sum(frame)
        if len(frame) == 1:  # Strike
            if f + 1 < len(frames):
                score += sum(frames[f + 1][:2])
                if len(frames[f + 1]) == 1 and f + 2 < len(frames):
                    score += frames[f + 2][0]
        elif frame[1] == 10 - frame[0]:  # Spare
            if f + 1 < len(frames):
                score += frames[f + 1][0]

    return score
