    def get_value(roll):
        if roll == 'X':
            return 10
        elif roll == '/':
            return 10
        elif roll == '-':
            return 0
        else:
            return int(roll)

    def is_strike(roll):
        return roll == 'X'

    def is_spare(roll):
        return roll == '/'

    score = 0
