            if i + 2 < len(rolls):
                if is_spare(rolls[i + 2]):
                    score += 10 - get_value(rolls[i + 1])
                else:
                    score += get_value(rolls[i + 2])
            i += 1
            frame += 1
        elif is_spare(rolls[i + 1]):
            score += 10
            if i + 2 < len(rolls):
                score += get_value(rolls[i + 2])
            i += 2
            frame += 1
        else:
            score += get_value(rolls[i]) + get_value(rolls[i + 1])
            i += 2
            frame += 1

    return score
