def my_func(input1: str):
    score = 0
    rolls = list(input1)
    frame = 0
    roll_index = 0

    while frame < 10:
        if rolls[roll_index] == 'X':  # Strike
            score += 10
            if roll_index + 1 < len(rolls):
                score += _roll_value(rolls[roll_index + 1])
            if roll_index + 2 < len(rolls):
                score += _roll_value(rolls[roll_index + 2])
            roll_index += 1
            frame += 1
        elif roll_index + 1 < len(rolls) and rolls[roll_index + 1] == '/':  # Spare
            score += 10
            if roll_index + 2 < len(rolls):
                score += _roll_value(rolls[roll_index + 2])
            roll_index += 2
            frame += 1
        else:  # Open frame
            score += _roll_value(rolls[roll_index]) + _roll_value(rolls[roll_index + 1])
            roll_index += 2
            frame += 1
    return score

def _roll_value(roll):
    if roll == 'X':
        return 10
    elif roll == '-':
        return 0
    elif roll == '/':
        return 10
    else:
        return int(roll)
