def my_func(input1: str):
    total_score = 0
    rolls = list(input1)
    frame = 0
    roll_index = 0

    while frame < 10:
        if rolls[roll_index] == 'X':  # Strike
            total_score += 10
            if roll_index + 1 < len(rolls):
                total_score += _score_of_roll(rolls[roll_index + 1])
            if roll_index + 2 < len(rolls):
                total_score += _score_of_roll(rolls[roll_index + 2])
            roll_index += 1
            frame += 1
        elif roll_index + 1 < len(rolls) and rolls[roll_index + 1] == '/':  # Spare
            total_score += 10
            if roll_index + 2 < len(rolls):
                total_score += _score_of_roll(rolls[roll_index + 2])
            roll_index += 2
            frame += 1
        else:  # Open frame
            total_score += _score_of_roll(rolls[roll_index])
            if roll_index + 1 < len(rolls):
                total_score += _score_of_roll(rolls[roll_index + 1])
            roll_index += 2
            frame += 1

    return total_score

def _score_of_roll(roll: str):
    if roll == 'X':
        return 10
    elif roll == '/':
        return 0
    elif roll == '-':
        return 0
    else:
        return int(roll)
