def my_func(input1: str):
    frames = []
    roll_index = 0

    while roll_index < len(input1):
        if len(frames) == 9:  # Last frame handling
            frames.append(input1[roll_index:])
            break

        if input1[roll_index] == 'X':  # Strike
            frames.append('X')
            roll_index += 1
        elif roll_index + 1 < len(input1):  # Spare or open frame
            frames.append(input1[roll_index:roll_index + 2])
            roll_index += 2

    def roll_value(roll):
        if roll == 'X':
            return 10
        elif roll == '/':
            return 10
        elif roll == '-':
            return 0
        else:
            return int(roll)

    score = 0
    for i in range(len(frames)):
        frame = frames[i]

        if 'X' in frame:  # Strike
            score += 10
            if i < len(frames) - 1:
                next_frame = frames[i + 1]
                if next_frame[0] == 'X':
                    score += 10
                    if i < len(frames) - 2:
                        second_next_frame = frames[i + 2]
                        score += roll_value(second_next_frame[0])
                    else:
                        score += roll_value(next_frame[1])
                else:
                    score += roll_value(next_frame[0]) + roll_value(next_frame[1])
        elif '/' in frame:  # Spare
            score += 10
            if i < len(frames) - 1:
                next_frame = frames[i + 1]
                score += roll_value(next_frame[0])
        else:  # Open frame
            score += roll_value(frame[0]) + roll_value(frame[1])

    return score
