def my_func(input1: str):
    frames = []
    roll_index = 0

    while roll_index < len(input1):
        if len(frames) == 10:
            break
        if input1[roll_index] == 'X':
            frames.append([10])
            roll_index += 1
        elif input1[roll_index + 1] == '/':
            frames.append([int(input1[roll_index]), 10 - int(input1[roll_index])])
            roll_index += 2
        else:
            frames.append([int(input1[roll_index]), int(input1[roll_index + 1])])
            roll_index += 2

    score = 0
    for i in range(len(frames)):
        if len(frames[i]) == 1:  # Strike
            score += 10
            if i + 1 < len(frames):
                score += frames[i + 1][0]
                if len(frames[i + 1]) == 1 and i + 2 < len(frames):
                    score += frames[i + 2][0]
                elif len(frames[i + 1]) > 1:
                    score += frames[i + 1][1]
        elif sum(frames[i]) == 10:  # Spare
            score += 10
            if i + 1 < len(frames):
                score += frames[i + 1][0]
        else:
            score += sum(frames[i])

    return score
