def my_func(input1: str):
    if len(input1) > 20 or len(input1) < 1:
        return ""

    def capitalize_after_dash(s):
        result = []
        capitalize_next = False
        for char in s:
            if char == '-':
                capitalize_next = True
            elif capitalize_next:
                result.append(char.upper())
                capitalize_next = False
            else:
                result.append(char)
        return ''.join(result)

    return ' '.join(capitalize_after_dash(word) for word in input1.split(' '))
