def my_func(input1: str):
    if len(input1) < 1 or len(input1) > 20:
        return None
    output = []
    for word in input1.split():
        new_word = []
        capitalize = False
        for char in word:
            if char == '-':
                capitalize = True
            else:
                new_word.append(char.upper() if capitalize else char)
                capitalize = False
        output.append(''.join(new_word))
    return ' '.join(output)
