def my_func(input1: str):
    if len(input1) < 1 or len(input1) > 20:
        return "Invalid input length"
    result = []
    capitalize_next = False
    for char in input1:
        if char == '-':
            capitalize_next = True
        elif capitalize_next:
            result.append(char.upper())
            capitalize_next = False
        else:
            result.append(char)
    return ''.join(result)
