def my_func(input1:str):
    if not (1 <= len(input1) <= 20):
        return ""

    def capitalize_after_dash(s):
        result = []
        capitalize_next = False
        for char in s:
            if char == '-':
                capitalize_next = True
            elif capitalize_next:
                result.append(char.upper())
                capitalize_next = False
            else:
                result.append(char)
        return ''.join(result)

    words = input1.split(' ')
    transformed_words = [capitalize_after_dash(word) for word in words]
    return ' '.join(transformed_words)
