def my_func(input1: str):
    if not 1 <= len(input1) <= 20:
        return ""
    words = input1.split()
    result = []
    for word in words:
        parts = word.split('-')
        transformed = ''.join(part.capitalize() if i > 0 else part for i, part in enumerate(parts))
        result.append(transformed)
    return ' '.join(result)
