def my_func(input1: str):
    if not (1 <= len(input1) <= 20):
        return ""

    def transform_segment(segment):
        parts = segment.split('-')
        return parts[0] + ''.join(part.capitalize() for part in parts[1:])

    words = input1.split(' ')
    transformed_words = [transform_segment(word) for word in words]
    return ' '.join(transformed_words)
