def my_func(input1: str):
    if len(input1) < 1 or len(input1) > 20:
        return input1
    result = ''
    words = input1.split()
    for word in words:
        parts = word.split('-')
        transformed_parts = [parts[0]] + [part.capitalize() for part in parts[1:]]
        result += ''.join(transformed_parts) + ' '
    return result.strip()
