def my_func(input1: str) -> str:
    if not (1 <= len(input1) <= 20):
        raise ValueError("Input length must be between 1 and 20 characters.")

    words = input1.split()
    result = []

    for word in words:
        parts = word.split('-')
        for i in range(1, len(parts)):
            parts[i] = parts[i].capitalize()
        result.append(''.join(parts))

    return ' '.join(result)
