def my_func(input1: str):
    if not (1 <= len(input1) <= 20):
        return ""
    result = []
    capitalize = False
    for char in input1:
        if char == '-':
            capitalize = True
        elif capitalize:
            result.append(char.upper())
            capitalize = False
        else:
            result.append(char)
    return ''.join(result)
