def my_func(input1: str):
    if len(input1) < 1 or len(input1) > 20:
        return ""
    words = input1.split()
    transformed_words = []
    for word in words:
        parts = word.split('-')
        transformed_word = ''.join(part.capitalize() if i > 0 else part for i, part in enumerate(parts))
        transformed_words.append(transformed_word)
    return ' '.join(transformed_words)
