def my_func(input1: str):
    if len(input1) < 1 or len(input1) > 20:
        return "Invalid input length"

    result = []
    i = 0
    while i < len(input1):
        if input1[i] == '-':
            i += 1
            if i < len(input1):
                result.append(input1[i].upper())
        else:
            result.append(input1[i])
        i += 1
    return ''.join(result)
