def my_func(input1:str):
    if len(input1) < 1 or len(input1) > 20:
        return ""
    parts = input1.split()
    for i in range(len(parts)):
        segments = parts[i].split('-')
        for j in range(1, len(segments)):
            segments[j] = segments[j].capitalize()
        parts[i] = ''.join(segments)
    return ' '.join(parts)
