def my_func(input1: str):
    if len(input1) < 1 or len(input1) > 20:
        return None
    segments = input1.split(' ')
    result_segments = []
    for segment in segments:
        parts = segment.split('-')
        transformed_parts = [parts[0]] + [part.capitalize() for part in parts[1:]]
        result_segments.append(''.join(transformed_parts))
    return ' '.join(result_segments)
