def my_func(input1:str):
    if len(input1) < 1 or len(input1) > 20:
        return ""
    output = []
    for part in input1.split():
        segments = part.split('-')
        transformed = ''.join(segment.capitalize() if i > 0 else segment for i, segment in enumerate(segments))
        output.append(transformed)
    return ' '.join(output)
