def my_func(input1: str):
    if not (1 <= len(input1) <= 20):
        raise ValueError("Input string length must be between 1 and 20 characters.")

    words = input1.split(' ')
    transformed_words = []

    for word in words:
        parts = word.split('-')
        transformed_word = parts[0]

        for part in parts[1:]:
            transformed_word += part.capitalize()

        transformed_words.append(transformed_word)

    return ' '.join(transformed_words)
