def my_func(input1: str):
    if not (1 <= len(input1) <= 20):
        return None

    result = []
    capitalize_next = False

    for char in input1:
        if char == '-':
            capitalize_next = True
        else:
            if capitalize_next:
                result.append(char.upper())
                capitalize_next = False
            else:
                result.append(char)

    return ''.join(result)
