def my_func(input1: str):
    if len(input1) < 1 or len(input1) > 20:
        return ""
    result = []
    capitalize = False
    for char in input1:
        if char == '-':
            capitalize = True
        else:
            result.append(char.upper() if capitalize else char)
            capitalize = False
    return ''.join(result)
