def my_func(input1: str, input2: str):
    black_pegs = 0
    white_pegs = 0

    # Convert strings to lists for easier manipulation
    code = list(input1)
    guess = list(input2)

    # First pass: Check for black pegs
    for i in range(4):
        if guess[i] == code[i]:
            black_pegs += 1
            code[i] = guess[i] = None  # Mark matched positions

    # Second pass: Check for white pegs
    for i in range(4):
        if guess[i] is not None:
            if guess[i] in code:
                white_pegs += 1
                code[code.index(guess[i])] = None  # Remove matched color

    return black_pegs, white_pegs
