def my_func(input1: str, input2: str):
    black_pegs = sum(a == b for a, b in zip(input1, input2))
    white_pegs = 0

    # Track colors in the code not matched in the exact position
    code_colors = {}
    guess_colors = {}

    for i in range(4):
        if input1[i] != input2[i]:
            code_colors[input1[i]] = code_colors.get(input1[i], 0) + 1
            guess_colors[input2[i]] = guess_colors.get(input2[i], 0) + 1

    for color in guess_colors:
        if color in code_colors:
            white_pegs += min(code_colors[color], guess_colors[color])

    return black_pegs, white_pegs
