def my_func(input1: str, input2: str):
    black_pegs = 0
    white_pegs = 0

    code_chars = list(input1)
    guess_chars = list(input2)

    for i in range(4):
        if code_chars[i] == guess_chars[i]:
            black_pegs += 1
            code_chars[i] = guess_chars[i] = None

    for i in range(4):
        if guess_chars[i] is not None and guess_chars[i] in code_chars:
            white_pegs += 1
            code_chars[code_chars.index(guess_chars[i])] = None

    return black_pegs, white_pegs
