def my_func(input1: str, input2: str):
    black_pegs = sum(c1 == c2 for c1, c2 in zip(input1, input2))

    input1_counts = {}
    input2_counts = {}

    for c1, c2 in zip(input1, input2):
        if c1 != c2:
            input1_counts[c1] = input1_counts.get(c1, 0) + 1
            input2_counts[c2] = input2_counts.get(c2, 0) + 1

    white_pegs = sum(min(input1_counts.get(c, 0), input2_counts.get(c, 0)) for c in input1_counts)

    return black_pegs, white_pegs
