def my_func(input1: str, input2: str):
    black_pegs = sum(a == b for a, b in zip(input1, input2))
    input1_counts = {c: input1.count(c) for c in set(input1)}
    input2_counts = {c: input2.count(c) for c in set(input2)}
    white_pegs = sum(min(input1_counts.get(c, 0), input2_counts.get(c, 0)) for c in input1_counts)
    white_pegs -= black_pegs
    return black_pegs, white_pegs
