def my_func(input1: str, input2: str):
    black_pegs = sum(c1 == c2 for c1, c2 in zip(input1, input2))

    unmatched_code = []
    unmatched_guess = []

    for i in range(4):
        if input1[i] != input2[i]:
            unmatched_code.append(input1[i])
            unmatched_guess.append(input2[i])

    white_pegs = 0
    for color in unmatched_guess:
        if color in unmatched_code:
            white_pegs += 1
            unmatched_code.remove(color)

    return black_pegs, white_pegs
