def my_func(input1: str):
    if not (2 <= len(input1) <= 20):
        raise ValueError("Input must be a string of digits of length between 2 and 20.")

    total = 0
    consecutive_count = 1

    for i in range(1, len(input1)):
        if input1[i] == input1[i - 1]:
            consecutive_count += 1
        else:
            if consecutive_count > 1:
                total += int(input1[i - 1]) * consecutive_count
            consecutive_count = 1

    if consecutive_count > 1:
        total += int(input1[-1]) * consecutive_count

    return total
