def my_func(input1: str):
    if not (2 <= len(input1) <= 20) or not input1.isdigit():
        raise ValueError("Input must be a string of digits of length [2, 20].")
    count = 0
    last_digit = input1[0]
    for digit in input1[1:]:
        if digit == last_digit:
            count += int(digit)
        last_digit = digit
    return count
