def my_func(input1: str):
    if not (2 <= len(input1) <= 20) or not input1.isdigit():
        return 0
    consecutive_sum = 0
    max_digit = 0
    count = 1
    for i in range(1, len(input1)):
        if input1[i] == input1[i - 1]:
            count += 1
        else:
            if count > 1:
                consecutive_sum += int(input1[i - 1]) * count
            count = 1
        max_digit = max(max_digit, int(input1[i - 1]))
    if count > 1:
        consecutive_sum += int(input1[-1]) * count
    return consecutive_sum + max_digit
