def my_func(input1: str):
    if not (2 <= len(input1) <= 20) or not input1.isdigit():
        raise ValueError("Input must be a string of digits with length between 2 and 20")

    digit_counts = {}
    for digit in input1:
        digit_counts[digit] = digit_counts.get(digit, 0) + 1

    result = 0
    for digit, count in digit_counts.items():
        if count > 1:
            result += int(digit) * (count - 1)

    return result
