def my_func(input1: str):
    if not input1.isdigit() or not (2 <= len(input1) <= 20):
        return 0
    total = 0
    count = 0
    prev_digit = None
    for char in input1:
        digit = int(char)
        if digit == prev_digit:
            count += 1
        else:
            count = 1
        if count == 2:
            total += digit
        elif count > 2:
            total += digit * 2
        prev_digit = digit
    return total
