def my_func(input1: str):
    if not input1.isdigit() or not (2 <= len(input1) <= 20):
        return 0
    count = {}
    for digit in input1:
        if digit in count:
            count[digit] += 1
        else:
            count[digit] = 1
    max_digit = max(count, key=lambda x: (count[x], x))
    if count[max_digit] == 1:
        return 0
    return int(max_digit) * (count[max_digit] - 1)
