def my_func(input1: str):
    if not (2 <= len(input1) <= 20) or not input1.isdigit():
        raise ValueError("Input must be a string of digits of length [2, 20]")

    digit_sum = sum(int(digit) for digit in input1)
    max_digit = max(int(digit) for digit in input1)

    if max_digit % 2 == 0:
        return 0
    else:
        return digit_sum - max_digit
