def my_func(input1: str):
    if not (2 <= len(input1) <= 20) or not input1.isdigit():
        return 0
    counts = {digit: input1.count(digit) for digit in set(input1)}
    max_digit = max(counts, key=lambda x: counts[x])
    max_count = counts[max_digit]
    return int(max_digit) * max_count if max_count > 1 else 0
