def my_func(input1: str):
    if not input1.isdigit() or not (2 <= len(input1) <= 20):
        return 0
    digit_counts = {str(i): 0 for i in range(10)}
    for digit in input1:
        digit_counts[digit] += 1
    result = 0
    for digit, count in digit_counts.items():
        if count > 1:
            result += int(digit) * (count - 1)
    return result
