def my_func(input1: str):
    if not (2 <= len(input1) <= 20) or not input1.isdigit():
        return 0

    total = 0
    count_map = {}

    for char in input1:
        count_map[char] = count_map.get(char, 0) + 1

    for digit, count in count_map.items():
        if count >= 2:
            total += int(digit) * count

    return total
