def my_func(input1: str):
    if len(input1) < 2 or len(input1) > 20 or not input1.isdigit():
        return 0

    digits = list(map(int, input1))
    max_digit = max(digits)
    min_digit = min(digits)

    sum_of_digits = sum(digits)

    if sum_of_digits % 2 == 0:
        return sum_of_digits // 2
    elif max_digit > min_digit * 2:
        return max_digit
    else:
        return min_digit
