def my_func(input1: str):
    if not (2 <= len(input1) <= 20) or not input1.isdigit():
        raise ValueError("Input must be a string of digits with length between 2 and 20.")
    total = 0
    count = 0
    prev_digit = ''
    for digit in input1:
        if digit == prev_digit:
            count += 1
        else:
            if count >= 2:
                total += int(prev_digit) * count
            count = 1
        prev_digit = digit
