def my_func(input1: str):
    stack = []
    for char in input1:
        if char in {'t', 'f'}:
            stack.append(char == 't')
        elif char == '|':
            if stack:
                operand1 = stack.pop()
                operand2 = stack.pop() if stack else False
                stack.append(operand1 or operand2)
        elif char == '&':
            if stack:
                operand1 = stack.pop()
                operand2 = stack.pop() if stack else True
                stack.append(operand1 and operand2)
    return stack[0] if stack else False
