def my_func(input1: str) -> bool:
    if not (1 <= len(input1) <= 20):
        raise ValueError("Input length must be between 1 and 20")

    input1 = input1.lower()
    input1 = input1.replace('t', 'True').replace('f', 'False')

    operators = {'|': 'or', '&': 'and'}
    for symbol, op in operators.items():
        input1 = input1.replace(symbol, f' {op} ')

    return eval(input1)
