    def evaluate(expr):
        stack = []
        i = 0
        while i < len(expr):
            if expr[i] == 't':
                stack.append(True)
            elif expr[i] == 'f':
                stack.append(False)
            elif expr[i] == '|':
                right = stack.pop()
                left = stack.pop()
                stack.append(left or right)
