def my_func(input1: str):
    if len(input1) == 0:
        return input1

    words = input1.split()
    result = []

    for word in words:
        if len(word) <= 4 or len(word) > 10:
            result.append(word)
        else:
            reverse_part = word[::-1]
            result.append(reverse_part)

    return ' '.join(result)
