def my_func(input1: str):
    if len(input1) > 20:
        return ""
    split_input = input1.split()
    result = []
    for segment in split_input:
        if len(segment) >= 5:
            result.append(segment[::-1])
        else:
            result.append(segment)
    return ' '.join(result)
