def my_func(input1: str):
    if len(input1) > 20:
        raise ValueError("Input length must be between 0 and 20.")
    words = input1.split(' ')
    result = []
    for word in words:
        if len(word) > 4:
            result.append(word[::-1])
        else:
            result.append(word)
    return ' '.join(result)
